import React, { useState, useEffect } from "react";
import axios from "axios";
import jwtDecode from "jwt-decode";

function App() {
  const [token, setToken] = useState(null);
  const [user, setUser] = useState(null);
  const [modules, setModules] = useState([]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  useEffect(() => {
    if (token) {
      const decoded = jwtDecode(token);
      setUser({ username: decoded.sub });
      fetchModules(token);
    }
  }, [token]);

  const fetchModules = async (token) => {
    try {
      const response = await axios.get("http://localhost:3001/api/modules", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setModules(response.data.filter((m) => m.enabled));
    } catch (err) {
      setError("Fehler beim Laden der Module");
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      const response = await axios.post(
        "http://localhost:3001/api/auth/login",
        { username, password }
      );
      setToken(response.data.access_token);
    } catch (err) {
      setError("Ungültiger Benutzername oder Passwort");
    }
  };

  if (!token) {
    return (
      <div>
        <h2>Login</h2>
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="Benutzername"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Passwort"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Anmelden</button>
        </form>
        {error && <p style={{ color: "red" }}>{error}</p>}
      </div>
    );
  }

  return (
    <div>
      <h1>Willkommen, {user?.username}</h1>
      <h3>Freigeschaltete Module:</h3>
      <ul>
        {modules.map((mod) => (
          <li key={mod.module}>{mod.module}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
