import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MitarbeiterDetail = ({ userId, onBack, onUserUpdate }) => {
    const [formData, setFormData] = useState({ fullName: '', email: '', roles: '' });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    useEffect(() => {
        const fetchUser = async () => {
            try {
                setLoading(true);
                setError('');
                setSuccess('');
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:3009/api/users/${userId}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const user = response.data;
                setFormData({
                    fullName: user.fullName || '',
                    email: user.email || '',
                    roles: user.roles ? user.roles.join(', ') : '',
                });
            } catch (err) {
                setError('Fehler beim Laden der Benutzerdaten.');
            } finally {
                setLoading(false);
            }
        };

        if (userId) {
            fetchUser();
        }
    }, [userId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            setError('');
            setSuccess('');
            const token = localStorage.getItem('token');
            const rolesArray = formData.roles.split(',').map(role => role.trim()).filter(Boolean);

            const response = await axios.put(
                `http://localhost:3009/api/users/${userId}`,
                { fullName: formData.fullName, email: formData.email, roles: rolesArray },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            setSuccess(response.data.message);
            onUserUpdate(response.data.user);

        } catch (err) {
            setError(err.response?.data?.message || 'Fehler beim Aktualisieren des Benutzers.');
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return <div className="loading-spinner">Lade Benutzerdaten...</div>;
    }

    return (
        <div className="mitarbeiter-detail">
            <button onClick={onBack} className="back-button">&larr; Zurück zur Liste</button>
            <h2>Benutzer bearbeiten</h2>
            <form onSubmit={handleSubmit} className="edit-form">
                <div className="form-group">
                    <label htmlFor="fullName">Vollständiger Name</label>
                    <input
                        type="text"
                        id="fullName"
                        name="fullName"
                        value={formData.fullName}
                        onChange={handleChange}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">E-Mail</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="roles">Rollen (kommagetrennt)</label>
                    <input
                        type="text"
                        id="roles"
                        name="roles"
                        value={formData.roles}
                        onChange={handleChange}
                    />
                </div>
                <button type="submit" disabled={loading} className="save-button">
                    {loading ? 'Speichern...' : 'Änderungen speichern'}
                </button>
            </form>
            {success && <div className="success-message">{success}</div>}
            {error && <div className="error-message">{error}</div>}
        </div>
    );
};

export default MitarbeiterDetail;
