import React, { useState, useEffect } from "react";
import axios from "axios";
import jwtDecode from "jwt-decode";

function App() {
  // Token aus localStorage laden
  const [token, setToken] = useState(() => {
    const savedToken = localStorage.getItem('token');
    if (savedToken) {
      console.log('Token loaded from localStorage:', savedToken);
      return savedToken;
    }
    return null;
  });

  const [user, setUser] = useState(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      console.log('User loaded from localStorage:', savedUser);
      return JSON.parse(savedUser);
    }
    return null;
  });

  const [modules, setModules] = useState([]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  // Token und User in localStorage speichern
  useEffect(() => {
    if (token) {
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(user));
    } else {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
    }
  }, [token, user]);

  useEffect(() => {
    if (token) {
      const decoded = jwtDecode(token);
      setUser({ username: decoded.sub });
      fetchModules(token);
    }
  }, [token]);

  const fetchModules = async (token) => {
    try {
      console.log('Fetching modules with token:', token);
      console.log('Fetching modules from:', 'http://localhost:3008/api/modules');
      const response = await axios.get("http://localhost:3008/api/modules", {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      console.log('Modules response:', response.data);
      setModules(response.data.filter((m) => m.enabled));
    } catch (err) {
      console.error('Error fetching modules:', err.response?.data || err.message);
      setError('Fehler beim Laden der Module: ' + (err.response?.data?.message || err.message));
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    console.log('Login attempt with:', { username, password });
    console.log('Logging in to:', 'http://localhost:3008/api/auth/login');

    if (!username || !password) {
      setError('Bitte füllen Sie Benutzername und Passwort aus');
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:3008/api/auth/login",
        { username, password },
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );
      console.log('Login successful:', response.data);

      // Token und User speichern
      setToken(response.data.token);
      setUser({ username: response.data.user.username, roles: response.data.user.roles });

      // Modules laden
      fetchModules(response.data.token);

      // Formular zurücksetzen
      setUsername("");
      setPassword("");
    } catch (err) {
      console.error('Login error:', err);

      // Detailliertere Fehlerbehandlung
      let errorMessage = "Login fehlgeschlagen";

      if (err.response) {
        // Server hat eine Antwort zurückgegeben
        errorMessage += `: ${err.response.status} ${err.response.statusText}`;
        if (err.response.data) {
          errorMessage += `: ${err.response.data.message || err.response.data.error}`;
        }
      } else if (err.request) {
        // Request wurde gesendet, aber keine Antwort erhalten
        errorMessage += ": Keine Verbindung zum Server";
      } else {
        // Fehler beim Erstellen der Anfrage
        errorMessage += ": " + err.message;
      }

      setError(errorMessage);
    }
  };

  // Token-Validierung
  useEffect(() => {
    if (token) {
      try {
        const decoded = jwtDecode(token);
        if (Date.now() >= decoded.exp * 1000) {
          console.log('Token expired');
          setToken(null);
          setUser(null);
        }
      } catch (err) {
        console.error('Invalid token:', err);
        setToken(null);
        setUser(null);
      }
    }
  }, [token]);

  if (!token) {
    console.log('No token present, showing login form');
    return (
      <div style={{
        maxWidth: '400px',
        margin: '2rem auto',
        padding: '2rem',
        border: '1px solid #ccc',
        borderRadius: '8px'
      }}>
        <h2 style={{ color: '#333' }}>Login</h2>
        <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
          <input
            type="text"
            placeholder="Benutzername"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            style={{ padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px' }}
          />
          <input
            type="password"
            placeholder="Passwort"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px' }}
          />
          <button 
            type="submit" 
            style={{
              padding: '0.5rem',
              background: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Anmelden
          </button>
        </form>
        {error && (
          <p style={{ 
            color: "red", 
            marginTop: '1rem',
            padding: '0.5rem',
            background: '#ffebee',
            borderRadius: '4px'
          }}>
            {error}
          </p>
        )}
      </div>
    );
  }

  return (
    <div>
      <h1>Willkommen, {user?.username}</h1>
      <h3>Freigeschaltete Module:</h3>
      {modules.length > 0 ? (
        <ul>
          {modules.map((mod) => (
            <li key={mod.name}>{mod.name}</li>
          ))}
        </ul>
      ) : (
        <p>Keine Module gefunden</p>
      )}
    </div>
  );
}

export default App;
