import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MitarbeiterDetail = ({ userId, onBack, onUserUpdated, onUserCreated }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(!!userId);
    const [error, setError] = useState('');
    const [activeTab, setActiveTab] = useState('allgemein');
    const [isEditing, setIsEditing] = useState(!userId);
    const [formData, setFormData] = useState({});

    useEffect(() => {
        const fetchUser = async () => {
            try {
                setLoading(true);
                setError('');
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:3009/api/users/${userId}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setUser(response.data);
                setFormData(response.data); // Initialize form data
            } catch (err) {
                setError('Fehler beim Laden der Mitarbeiterdaten.');
            } finally {
                setLoading(false);
            }
        };

        if (userId) {
            fetchUser();
        }
    }, [userId]);

    const handleEdit = (e) => {
        e.preventDefault(); // Prevent default form submission behavior
        setFormData(user); // Reset form data to current user state
        setIsEditing(true);
    };

    const handleCancel = () => {
        setIsEditing(false);
        if (userId) {
            setFormData(user);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        const keys = name.split('.');
        if (keys.length > 1) {
            setFormData(prev => ({
                ...prev,
                [keys[0]]: {
                    ...prev[keys[0]],
                    [keys[1]]: value
                }
            }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const headers = { Authorization: `Bearer ${token}` };

            if (userId) {
                // Update existing user
                const res = await axios.put(`http://localhost:3009/api/users/${userId}`, formData, { headers });
                setUser(res.data.user || res.data);
                if (onUserUpdated) onUserUpdated(res.data.user || res.data);
            } else {
                // Create new user
                const res = await axios.post('http://localhost:3009/api/users', formData, { headers });
                if (onUserCreated) onUserCreated(res.data);
            }
            setIsEditing(false);
            if (!userId) onBack();
        } catch (err) {
            setError(err.response?.data?.message || 'Fehler beim Speichern der Daten.');
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        // Returns date in YYYY-MM-DD format for input[type=date]
        return new Date(dateString).toISOString().split('T')[0];
    };

    const displayDate = (dateString) => {
        if (!dateString) return 'N/A';
        return new Date(dateString).toLocaleDateString('de-DE');
    }

    if (loading) return <div className="loading-spinner">Lade Stammdaten...</div>;
    if (error) return <div className="error-message">{error}</div>;
    if (userId && !user) return <div>Keine Benutzerdaten gefunden.</div>;

    return (
        <form className="stammdaten-ansicht" onSubmit={handleSubmit}>
            <div className="header-bar">
                <button type="button" onClick={onBack} className="back-button">&larr; Zurück zur Liste</button>
                <h2>{userId ? `Mitarbeiterstammdaten: ${user?.fullName || ''}` : 'Neuen Mitarbeiter anlegen'}</h2>
                <div className="actions">
                    {(user?.username !== 'admin' || !userId) && (
                        isEditing ? (
                            <>
                                <button type="submit" className="action-button save">Speichern</button>
                                <button type="button" onClick={handleCancel} className="action-button cancel">Abbrechen</button>
                            </>
                        ) : (
                            <button type="button" onClick={handleEdit} className="action-button edit">Bearbeiten &#9998;</button>
                        )
                    )}
                </div>
            </div>

            <div className="kartei-tabs">
                <button type="button" className={`tab-button ${activeTab === 'allgemein' ? 'active' : ''}`} onClick={() => setActiveTab('allgemein')}>Allgemein</button>
                <button type="button" className={`tab-button ${activeTab === 'hr' ? 'active' : ''}`} onClick={() => setActiveTab('hr')}>HR-Daten</button>
            </div>

            <div className="kartei-content">
                {activeTab === 'allgemein' && (
                    <div className="stammdaten-grid">
                        <div className="stammdaten-sektion">
                            <h3>Persönliche Daten</h3>
                            <div className="daten-paar"><span className="label">Vollständiger Name</span>{isEditing ? <input type="text" name="fullName" value={formData.fullName || ''} onChange={handleChange} /> : <span>{user?.fullName || 'N/A'}</span>}</div>
                            <div className="daten-paar"><span className="label">Geburtsdatum</span>{isEditing ? <input type="date" name="geburtsdatum" value={formatDate(formData.geburtsdatum)} onChange={handleChange} /> : <span>{displayDate(user?.geburtsdatum)}</span>}</div>
                            <div className="daten-paar"><span className="label">Straße</span>{isEditing ? <input type="text" name="adresse.strasse" value={formData.adresse?.strasse || ''} onChange={handleChange} /> : <span>{user?.adresse?.strasse || 'N/A'}</span>}</div>
                            <div className="daten-paar"><span className="label">PLZ</span>{isEditing ? <input type="text" name="adresse.plz" value={formData.adresse?.plz || ''} onChange={handleChange} /> : <span>{user?.adresse?.plz || 'N/A'}</span>}</div>
                            <div className="daten-paar"><span className="label">Stadt</span>{isEditing ? <input type="text" name="adresse.stadt" value={formData.adresse?.stadt || ''} onChange={handleChange} /> : <span>{user?.adresse?.stadt || 'N/A'}</span>}</div>
                        </div>
                        <div className="stammdaten-sektion">
                            <h3>Kontaktdaten</h3>
                            <div className="daten-paar"><span className="label">E-Mail</span>{isEditing ? <input type="email" name="email" value={formData.email || ''} onChange={handleChange} /> : <span>{user?.email || 'N/A'}</span>}</div>
                            <div className="daten-paar"><span className="label">Benutzername</span><span>{user?.username}</span></div>
                        </div>
                        <div className="stammdaten-sektion">
                            <h3>Unternehmensdaten</h3>
                            <div className="daten-paar"><span className="label">Position</span>{isEditing ? <input type="text" name="position" value={formData.position || ''} onChange={handleChange} /> : <span>{user?.position || 'N/A'}</span>}</div>
                            <div className="daten-paar"><span className="label">Abteilung</span>{isEditing ? <input type="text" name="abteilung" value={formData.abteilung || ''} onChange={handleChange} /> : <span>{user?.abteilung || 'N/A'}</span>}</div>
                            <div className="daten-paar"><span className="label">Eintrittsdatum</span>{isEditing ? <input type="date" name="eintrittsdatum" value={formatDate(formData.eintrittsdatum)} onChange={handleChange} /> : <span>{displayDate(user?.eintrittsdatum)}</span>}</div>
                            <div className="daten-paar"><span className="label">Status</span>{isEditing ? <select name="status" value={formData.status || 'aktiv'} onChange={handleChange}><option value="aktiv">Aktiv</option><option value="inaktiv">Inaktiv</option><option value="beurlaubt">Beurlaubt</option></select> : <span className={`status-badge status-${user?.status}`}>{user?.status}</span>}</div>
                        </div>
                    </div>
                )}
                {activeTab === 'hr' && (
                    <div className="stammdaten-grid">
                        <div className="stammdaten-sektion">
                            <h3>Finanzdaten</h3>
                            <div className="daten-paar"><span className="label">Gehalt</span>{isEditing ? <input type="number" name="gehalt" value={formData.gehalt || ''} onChange={handleChange} /> : <span>{user?.gehalt ? `${user.gehalt.toLocaleString('de-DE')} €` : 'N/A'}</span>}</div>
                            <div className="daten-paar"><span className="label">IBAN</span>{isEditing ? <input type="text" name="iban" value={formData.iban || ''} onChange={handleChange} /> : <span>{user?.iban || 'N/A'}</span>}</div>
                        </div>
                    </div>
                )}
            </div>
        </form>
    );
};

export default MitarbeiterDetail;
