import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MitarbeiterDetail = ({ userId, onBack }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [activeTab, setActiveTab] = useState('allgemein');

    useEffect(() => {
        const fetchUser = async () => {
            try {
                setLoading(true);
                setError('');
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:3009/api/users/${userId}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setUser(response.data);
            } catch (err) {
                setError('Fehler beim Laden der Mitarbeiterdaten.');
            } finally {
                setLoading(false);
            }
        };

        if (userId) {
            fetchUser();
        }
    }, [userId]);

    const formatDate = (dateString) => {
        if (!dateString) return 'N/A';
        return new Date(dateString).toLocaleDateString('de-DE');
    };

    if (loading) {
        return <div className="loading-spinner">Lade Stammdaten...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    if (!user) {
        return <div>Keine Benutzerdaten gefunden.</div>;
    }

    return (
        <div className="stammdaten-ansicht">
            <button onClick={onBack} className="back-button">&larr; Zurück zur Liste</button>
            <h2>Mitarbeiterstammdaten: {user.fullName || ''}</h2>

            <div className="kartei-tabs">
                <button 
                    className={`tab-button ${activeTab === 'allgemein' ? 'active' : ''}`}
                    onClick={() => setActiveTab('allgemein')}>
                    Allgemein
                </button>
                <button 
                    className={`tab-button ${activeTab === 'hr' ? 'active' : ''}`}
                    onClick={() => setActiveTab('hr')}>
                    HR-Daten
                </button>
            </div>

            <div className="kartei-content">
                {activeTab === 'allgemein' && (
                    <div className="stammdaten-grid">
                        <div className="stammdaten-sektion">
                            <h3>Persönliche Daten</h3>
                            <div className="daten-paar"><span className="label">Vollständiger Name</span><span>{user.fullName || 'N/A'}</span></div>
                            <div className="daten-paar"><span className="label">Geburtsdatum</span><span>{formatDate(user.geburtsdatum)}</span></div>
                            <div className="daten-paar"><span className="label">Adresse</span><span>{`${user.adresse?.strasse || ''} ${user.adresse?.plz || ''} ${user.adresse?.stadt || 'N/A'}`}</span></div>
                        </div>

                        <div className="stammdaten-sektion">
                            <h3>Kontaktdaten</h3>
                            <div className="daten-paar"><span className="label">E-Mail</span><span>{user.email || 'N/A'}</span></div>
                            <div className="daten-paar"><span className="label">Benutzername</span><span>{user.username}</span></div>
                        </div>

                        <div className="stammdaten-sektion">
                            <h3>Unternehmensdaten</h3>
                            <div className="daten-paar"><span className="label">Position</span><span>{user.position || 'N/A'}</span></div>
                            <div className="daten-paar"><span className="label">Abteilung</span><span>{user.abteilung || 'N/A'}</span></div>
                            <div className="daten-paar"><span className="label">Eintrittsdatum</span><span>{formatDate(user.eintrittsdatum)}</span></div>
                            <div className="daten-paar"><span className="label">Status</span><span className={`status-badge status-${user.status}`}>{user.status}</span></div>
                            <div className="daten-paar"><span className="label">Rollen</span><span>{user.roles?.join(', ') || 'N/A'}</span></div>
                        </div>
                    </div>
                )}

                {activeTab === 'hr' && (
                     <div className="stammdaten-grid">
                        <div className="stammdaten-sektion">
                            <h3>Finanzdaten</h3>
                            <div className="daten-paar"><span className="label">Gehalt</span><span>{user.gehalt ? `${user.gehalt.toLocaleString('de-DE')} €` : 'N/A'}</span></div>
                            <div className="daten-paar"><span className="label">IBAN</span><span>{user.iban || 'N/A'}</span></div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default MitarbeiterDetail;
