import React from 'react';

const MainContent = ({ module }) => {
    if (!module) {
        return (
            <main className="main-content">
                <div className="content-header">
                    <h1>Willkommen</h1>
                </div>
                <div className="content-body">
                    <p>Bitte wählen Sie ein Modul aus der Seitenleiste aus.</p>
                </div>
            </main>
        );
    }

    return (
        <main className="main-content">
            <div className="content-header">
                <h1>{module.name}</h1>
            </div>
            <div className="content-body">
                <p>Hier werden die Inhalte für das Modul "{module.name}" angezeigt.</p>
                {/* Hier können später die spezifischen Komponenten für jedes Modul geladen werden */}
            </div>
        </main>
    );
};

export default MainContent;
